typedef struct ip_header // 定义 IPv4 报文的首部格式，总共占 20 个字节，RFC 791
{
 unsigned char ip_ver_and_ip_hdrlen ; // ip_ver：版本号，占高 4 位。定义 IP 协议的版本。通信双方所使用的版本必须一致，
          // 一般的值为0100（IPv4）；
          // ip_hdrlen：IP 首部长度（Header Length），占低 4 位。定义 IP 数据报首部除以
          // 4B 计算的总长度。取值范围为 [5，15]
 unsigned char  ip_tos ;       // ip_tos：服务类型，占 8 位，定义路由器应如何处理此 IP 数据报。包括优先级和服务要求两个部分
 unsigned short ip_total_len ; // ip_total_len：总长度，占 16 位，定义整个 IP 数据报的长度（首部+数据），单位为字节（Byte）
 unsigned short ip_id ;        // ip_id：标识，占 16 位，用于标识不同的 IP 数据报

 unsigned short ip_flag_and_ip_offset ; // ip_flag：标志，占 3 位，ip_offset：片偏移，占 13 位

#define IP_DF 0x4000 /* don't fragment flag */
#define IP_MF 0x2000 /* more fragments flag */

 unsigned char  ip_ttl ;      // ip_ttl：生存时间，占 8 位，IP 数据报在通过 Internet 时所具有的寿命，
 unsigned char  ip_protocol ; // ip_prptocol：协议，占 8 位。定义使用 IP 服务的高层协议。用来指出 IP 层所封装的上层协议类型，
         // 例如：UDP(17)、TCP(6）、ICMP（1）、IGMP(2) 等，若定义为 char，则在后面的 printf 输出时不需使用 ntohl（或者 ntohs）函数进行转换
 unsigned short ip_checksum ; // ip_checksum：首部校验和，占 16 位
 unsigned int   source_ip ;   // source_ip：源 IP 地址，占 32 位
 unsigned int   dest_ip ;     // dest_ip：目的 IP 地址，占 32 位
} IP_HEADER ;

